import React, { useEffect, useState } from "react";
import { ChevronLeft, ChevronRight, Sun, Moon, Share } from "lucide-react";

const Header = ({
  darkMode,
  setDarkMode,
  sidebarCollapsed,
  setSidebarCollapsed,
}) => {
  const [user, setUser] = useState(null);

  useEffect(() => {
    // Get user info from localStorage on component mount
    const token = localStorage.getItem("token");
    if (token) {
      try {
        // Parse the JWT to get user info (not secure, but simple)
        const base64Url = token.split(".")[1];
        const base64 = base64Url.replace(/-/g, "+").replace(/_/g, "/");
        const jsonPayload = decodeURIComponent(
          atob(base64)
            .split("")
            .map(function (c) {
              return "%" + ("00" + c.charCodeAt(0).toString(16)).slice(-2);
            })
            .join("")
        );

        const userData = JSON.parse(jsonPayload);
        setUser(userData);
      } catch (error) {
        console.error("Error parsing token", error);
      }
    }
  }, []);

  // Function to get user's initial
  const getUserInitial = () => {
    if (user && user.name) {
      return user.name.charAt(0).toUpperCase();
    }
    return "U"; // Default if no name is available
  };

  // Function to generate a color based on user's email (for consistent colors)
  const getUserColor = () => {
    if (!user || !user.email) return "bg-blue-500";

    // Simple hash function to generate a color
    const hash = user.email.split("").reduce((acc, char) => {
      return char.charCodeAt(0) + acc;
    }, 0);

    const colors = [
      "bg-blue-500",
      "bg-green-500",
      "bg-purple-500",
      "bg-red-500",
      "bg-yellow-500",
      "bg-pink-500",
    ];

    return colors[hash % colors.length];
  };

  return (
    <header
      className={`border-b ${
        darkMode ? "border-gray-700" : "border-gray-200"
      } py-1.5 px-3 flex items-center justify-between`}
    >
      <div className="flex items-center">
        <button
          onClick={() => setSidebarCollapsed(!sidebarCollapsed)}
          className={`p-1.5 rounded-full ${
            darkMode ? "hover:bg-gray-700" : "hover:bg-gray-100"
          }`}
        >
          {sidebarCollapsed ? (
            <ChevronRight size={16} />
          ) : (
            <ChevronLeft size={16} />
          )}
        </button>
      </div>

      <div className="flex items-center">
        <button
          className={`p-1.5 rounded-full ${
            darkMode ? "hover:bg-gray-700" : "hover:bg-gray-100"
          } mr-1.5`}
          onClick={() => setDarkMode(!darkMode)}
        >
          {darkMode ? <Sun size={16} /> : <Moon size={16} />}
        </button>
        <button
          className={`p-1.5 rounded-full ${
            darkMode ? "hover:bg-gray-700" : "hover:bg-gray-100"
          } mr-1.5`}
        >
          <Share size={16} className={darkMode ? "text-gray-300" : ""} />
        </button>
        <div
          className={`w-5 h-5 ${getUserColor()} rounded-full flex items-center justify-center text-white text-xs font-medium`}
        >
          {getUserInitial()}
        </div>
      </div>
    </header>
  );
};

export default Header;
