import React, { useState } from "react";
import {
  MessageSquare,
  Image,
  HelpCircle,
  FileText,
  ChevronDown,
  Smartphone,
  Award,
  Edit2,
  Trash2,
} from "lucide-react";

const Sidebar = ({
  darkMode,
  sidebarCollapsed,
  setSidebarCollapsed,
  showMore,
  setShowMore,
  onNewChat,
  chats,
  onSelectChat,
  activeChatId,
  onEditChatTitle,
  onDeleteChat,
}) => {
  const [editingChatId, setEditingChatId] = useState(null);
  const [editTitle, setEditTitle] = useState("");

  const handleNewChat = () => {
    if (onNewChat) onNewChat();
  };

  const startEditing = (chat) => {
    setEditingChatId(chat._id);
    setEditTitle(chat.title);
  };

  const saveEdit = (chatId) => {
    if (editTitle.trim()) {
      onEditChatTitle(chatId, editTitle);
    }
    setEditingChatId(null);
    setEditTitle("");
  };

  const handleKeyDown = (e, chatId) => {
    if (e.key === "Enter") {
      saveEdit(chatId);
    } else if (e.key === "Escape") {
      setEditingChatId(null);
      setEditTitle("");
    }
  };

  return (
    <div
      className={`${darkMode ? "bg-gray-50" : "bg-white"} ${
        sidebarCollapsed ? "w-16" : "w-64"
      } flex flex-col border-r border-gray-100 transition-all duration-300 h-full`}
    >
      <div className="p-4 flex items-center">
        <div className="bg-blue-600 text-white p-2 rounded-full mr-3">
          <MessageSquare size={20} />
        </div>
        {!sidebarCollapsed && (
          <span className="font-bold text-lg">BotAI</span>
        )}
      </div>

      <div className="px-3 py-2">
        <button
          className="bg-blue-600 hover:bg-blue-700 text-white rounded-lg p-3 w-full flex items-center justify-center"
          onClick={handleNewChat}
        >
          {sidebarCollapsed ? (
            <MessageSquare size={18} />
          ) : (
            <>
              <MessageSquare size={18} className="mr-2" />
              <span className="text-base font-medium">New Chat</span>
            </>
          )}
        </button>
      </div>

      <div className={`px-3 py-2 ${sidebarCollapsed ? "items-center" : ""}`}>
        <div
          className={`flex items-center p-2 rounded-lg hover:bg-gray-100 cursor-pointer`}
        >
          <Image
            size={18}
            className="mr-3 text-gray-500"
          />
          {!sidebarCollapsed && <span className="text-base text-gray-800">AI Generator</span>}
        </div>
        <div
          className={`flex items-center p-2 rounded-lg hover:bg-gray-100 cursor-pointer`}
        >
          <HelpCircle
            size={18}
            className="mr-3 text-gray-500"
          />
          {!sidebarCollapsed && <span className="text-base text-gray-800">Explore BotAI</span>}
        </div>
      </div>

      {!sidebarCollapsed && (
        <div className="flex flex-col flex-1 overflow-hidden">
          <div className="px-3 pt-3 pb-1 sticky top-0 bg-inherit z-10">
            <div className="text-sm text-gray-500 font-medium">
              Chats
            </div>
          </div>
          
          <div className="px-3 overflow-y-auto flex-1 pb-2">
            {chats.map((chat) => (
              <div
                key={chat._id}
                onClick={() => onSelectChat(chat._id)}
                className={`flex items-center p-2 rounded-lg group ${
                  activeChatId === chat._id
                    ? "bg-gray-100"
                    : "hover:bg-gray-50"
                } cursor-pointer relative mb-1`}
              >
                {editingChatId === chat._id ? (
                  <input
                    type="text"
                    value={editTitle}
                    onChange={(e) => setEditTitle(e.target.value)}
                    onKeyDown={(e) => handleKeyDown(e, chat._id)}
                    onBlur={() => saveEdit(chat._id)}
                    className="flex-1 text-sm p-1 rounded bg-white text-black border border-gray-200"
                    autoFocus
                  />
                ) : (
                  <>
                    <FileText
                      size={16}
                      className="mr-3 text-gray-500"
                    />
                    <span
                      className="text-base text-gray-800 truncate flex-1"
                    >
                      {chat.title}
                    </span>
                    <div className="opacity-0 group-hover:opacity-100 flex space-x-1">
                      <button
                        onClick={(e) => {
                          e.stopPropagation();
                          startEditing(chat);
                        }}
                        className="p-1 rounded-full hover:bg-gray-200"
                      >
                        <Edit2
                          size={14}
                          className="text-gray-500"
                        />
                      </button>
                      <button
                        onClick={(e) => {
                          e.stopPropagation();
                          onDeleteChat(chat._id);
                        }}
                        className="p-1 rounded-full hover:bg-gray-200"
                      >
                        <Trash2
                          size={14}
                          className="text-gray-500"
                        />
                      </button>
                    </div>
                  </>
                )}
              </div>
            ))}
          </div>
        </div>
      )}

      <div className={`px-2 py-2 border-t ${darkMode ? "border-gray-700" : "border-gray-200"}`}>
        <button className="bg-blue-50 text-blue-600 hover:bg-blue-100 rounded-lg p-1.5 w-full flex items-center justify-center">
          {sidebarCollapsed ? (
            <Smartphone size={16} />
          ) : (
            <>
              <Smartphone size={16} className="mr-1.5" />
              <span className="text-xs">Get App</span>
              <span className="ml-auto text-xs bg-blue-100 px-1 py-0.5 rounded">
                New
              </span>
            </>
          )}
        </button>
      </div>
    </div>
  );
};

export default Sidebar;
