import React, { useState, useEffect, useRef } from "react";
import {
  MessageSquare,
  Search,
  Lightbulb,
  Mic,
  PenLine,
  Send,
  Image,
  Video,
  Music,
  Edit,
  BookOpen,
  FileCode,
  FileText,
  User,
  Bot,
  CheckCheck,
} from "lucide-react";

const Chat = ({ darkMode = false }) => {
  const [message, setMessage] = useState("");
  const [chat, setChat] = useState([
    {
      type: "bot",
      content: "Hello! I'm BotAI. How can I help you today?",
      time: "10:30 AM",
    },
  ]);
  const [isTyping, setIsTyping] = useState(false);

  const messagesEndRef = useRef(null);

  const scrollToBottom = () => {
    messagesEndRef.current?.scrollIntoView({ behavior: "smooth" });
  };

  useEffect(() => {
    scrollToBottom();
  }, [chat]);

  const getCurrentTime = () => {
    const now = new Date();
    return now.toLocaleTimeString("en-US", {
      hour: "numeric",
      minute: "2-digit",
      hour12: true,
    });
  };

  const handleSendMessage = () => {
    if (message.trim()) {
      // Add user message
      const userMessage = {
        type: "user",
        content: message,
        time: getCurrentTime(),
      };

      setChat((prevChat) => [...prevChat, userMessage]);
      setMessage("");

      // Show typing indicator
      setIsTyping(true);

      // Simulate response after a delay
      setTimeout(() => {
        setIsTyping(false);

        // Add bot response
        const botResponse = {
          type: "bot",
          content: getReply(message),
          time: getCurrentTime(),
        };

        setChat((prevChat) => [...prevChat, botResponse]);
      }, 1500);
    }
  };

  const getReply = (msg) => {
    // Static reply for all messages
    return "Thank you for your message. Our team will review it and get back to you soon. Is there anything else I can help you with?";
  };

  const handleKeyPress = (e) => {
    if (e.key === "Enter" && !e.shiftKey) {
      e.preventDefault();
      handleSendMessage();
    }
  };

  return (
    <main
      className={`flex-1 flex flex-col h-full ${
        darkMode ? "bg-gray-900 text-white" : "bg-gray-50"
      }`}
    >
      {/* Chat messages area */}
      <div className="flex-1 overflow-y-auto p-4">
        {chat.length === 0 ? (
          <div className="flex flex-col items-center justify-center h-full">
            <div className="bg-blue-600 text-white p-3 rounded-full mb-3">
              <MessageSquare size={20} />
            </div>
            <h1 className="text-xl font-bold mb-1">Hello, I'm BotAI</h1>
            <p
              className={`text-sm ${
                darkMode ? "text-gray-400" : "text-gray-500"
              }`}
            >
              How can I make things easier for you?
            </p>
          </div>
        ) : (
          <div className="space-y-4">
            {chat.map((msg, index) => (
              <div
                key={index}
                className={`flex ${
                  msg.type === "user" ? "justify-end" : "justify-start"
                }`}
              >
                <div
                  className={`max-w-xs md:max-w-md lg:max-w-lg rounded-lg px-4 py-2 ${
                    msg.type === "user"
                      ? "bg-blue-600 text-white rounded-br-none"
                      : darkMode
                      ? "bg-gray-800 text-white rounded-bl-none"
                      : "bg-white border border-gray-200 rounded-bl-none"
                  }`}
                >
                  <div className="flex items-center mb-1">
                    <div className="mr-2">
                      {msg.type === "user" ? (
                        <User size={14} className="text-blue-200" />
                      ) : (
                        <Bot
                          size={14}
                          className={
                            darkMode ? "text-gray-400" : "text-gray-500"
                          }
                        />
                      )}
                    </div>
                    <span className="text-xs font-medium">
                      {msg.type === "user" ? "You" : "BotAI"}
                    </span>
                    <span className="text-xs ml-auto opacity-70">
                      {msg.time}
                    </span>
                  </div>
                  <p className="text-sm whitespace-pre-wrap">{msg.content}</p>
                  {msg.type === "user" && (
                    <div className="flex justify-end mt-1">
                      <CheckCheck size={12} className="text-blue-200" />
                    </div>
                  )}
                </div>
              </div>
            ))}

            {isTyping && (
              <div className="flex justify-start">
                <div
                  className={`max-w-xs rounded-lg px-4 py-2 ${
                    darkMode
                      ? "bg-gray-800 text-white rounded-bl-none"
                      : "bg-white border border-gray-200 rounded-bl-none"
                  }`}
                >
                  <div className="flex items-center space-x-1">
                    <div className="w-2 h-2 rounded-full bg-gray-400 animate-bounce"></div>
                    <div
                      className="w-2 h-2 rounded-full bg-gray-400 animate-bounce"
                      style={{ animationDelay: "0.2s" }}
                    ></div>
                    <div
                      className="w-2 h-2 rounded-full bg-gray-400 animate-bounce"
                      style={{ animationDelay: "0.4s" }}
                    ></div>
                  </div>
                </div>
              </div>
            )}
            <div ref={messagesEndRef} />
          </div>
        )}
      </div>

      {/* Tools grid */}
      {chat.length === 0 && (
        <div className="w-full px-4 py-6 grid grid-cols-2 sm:grid-cols-3 gap-2">
          {[
            { icon: Image, text: "Image Generator" },
            { icon: Video, text: "Video Generator" },
            { icon: Music, text: "Audio Generator" },
            { icon: Edit, text: "Photo Editor" },
            { icon: BookOpen, text: "Education Feedback" },
            { icon: MessageSquare, text: "Get Advice" },
            { icon: FileCode, text: "Code Generator" },
            { icon: PenLine, text: "Help me write" },
            { icon: FileText, text: "Summarize text" },
          ].map((item, index) => (
            <div
              key={index}
              className={`flex items-center p-2 rounded-lg border cursor-pointer hover:shadow-sm ${
                darkMode
                  ? "bg-gray-800 border-gray-700 hover:bg-gray-700"
                  : "bg-white border-gray-200 hover:bg-gray-50"
              }`}
            >
              <item.icon
                size={16}
                className={`mr-1.5 ${
                  darkMode ? "text-gray-400" : "text-gray-500"
                }`}
              />
              <span className={`text-xs ${darkMode ? "text-gray-300" : ""}`}>
                {item.text}
              </span>
            </div>
          ))}
        </div>
      )}

      {/* Input area */}
      <div className="p-4">
        <div
          className={`w-full relative rounded-lg shadow-sm border ${
            darkMode
              ? "bg-gray-800 border-gray-700"
              : "bg-white border-gray-200"
          } overflow-hidden`}
        >
          <div className="p-3 min-h-16">
            <textarea
              className={`w-full h-full outline-none resize-none text-sm ${
                darkMode ? "bg-gray-800 text-white placeholder-gray-400" : ""
              }`}
              placeholder="Message BotAI..."
              value={message}
              onChange={(e) => setMessage(e.target.value)}
              onKeyDown={handleKeyPress}
              rows={1}
            ></textarea>
          </div>

          <div
            className={`border-t ${
              darkMode ? "border-gray-700" : "border-gray-200"
            } p-2 flex items-center justify-between`}
          >
            <div className="flex">
              <button
                className={`p-1.5 rounded-full ${
                  darkMode ? "hover:bg-gray-700" : "hover:bg-gray-100"
                }`}
              >
                <Search
                  size={16}
                  className={darkMode ? "text-gray-400" : "text-gray-500"}
                />
              </button>
              <button
                className={`p-1.5 rounded-full ${
                  darkMode ? "hover:bg-gray-700" : "hover:bg-gray-100"
                }`}
              >
                <Lightbulb
                  size={16}
                  className={darkMode ? "text-gray-400" : "text-gray-500"}
                />
              </button>
            </div>

            <div className="flex">
              <button
                className={`p-1.5 rounded-full ${
                  darkMode ? "hover:bg-gray-700" : "hover:bg-gray-100"
                }`}
              >
                <Mic
                  size={16}
                  className={darkMode ? "text-gray-400" : "text-gray-500"}
                />
              </button>
              <button
                className={`p-1.5 rounded-full ${
                  darkMode ? "hover:bg-gray-700" : "hover:bg-gray-100"
                }`}
              >
                <PenLine
                  size={16}
                  className={darkMode ? "text-gray-400" : "text-gray-500"}
                />
              </button>
              <button
                className="bg-blue-600 text-white p-1.5 rounded-full hover:bg-blue-700"
                onClick={handleSendMessage}
              >
                <Send size={16} />
              </button>
            </div>
          </div>
        </div>
      </div>
    </main>
  );
};

export default Chat;
