import React, { useContext } from "react";
import { AuthContext } from "../Context/AuthContext";
import { Link } from "react-router-dom";

function SideBar() {
  const { token } = useContext(AuthContext);

  return (
    <>
      <div className="w-64 h-screen bg-gray-900 text-white flex flex-col">
        <div className="p-4 border-b border-gray-700">
          <button className="w-full bg-gray-700 hover:bg-gray-600 text-white py-2 px-4 rounded-md flex items-center gap-2">
            <span>+</span> New Chat
          </button>
        </div>

        <div className="flex-1 overflow-y-auto">
          {/* Chat history would go here */}
          <div className="p-4 hover:bg-gray-800 cursor-pointer">
            Previous Chat 1
          </div>
          <div className="p-4 hover:bg-gray-800 cursor-pointer">
            Previous Chat 2
          </div>
        </div>
        {token && (
          <div className="p-4 border-t border-gray-700">
            <Link
              to="/Dash-Board/profile" // Updated to match the nested route
              className="flex items-center gap-2 hover:bg-gray-800 p-2 rounded-md"
            >
              <div className="w-8 h-8 rounded-full bg-gray-600 flex items-center justify-center">
                <span>U</span>
              </div>
              <span>Profile</span>
            </Link>
          </div>
        )}
      </div>
    </>
  );
}

export default SideBar;
