import React, { useState, useRef, useEffect } from "react";
import {
  MessageSquare,
  Search,
  Lightbulb,
  Mic,
  PenLine,
  Send,
  Image,
  Video,
  Music,
  Edit,
  BookOpen,
  FileCode,
  FileText,
  Check,
  CheckCheck,
  ThumbsUp,
  ThumbsDown,
  Copy,
  CornerUpRight,
} from "lucide-react";
import axios from "axios";

const Chat = ({
  darkMode,
  newChatTrigger = 0,
  currentChatId,
  setCurrentChatId,
}) => {
  const [inputValue, setInputValue] = useState("");
  const [messages, setMessages] = useState([]);
  const [showWelcome, setShowWelcome] = useState(true);
  const [copyNotification, setCopyNotification] = useState(null);
  const messagesEndRef = useRef(null);
  const inputRef = useRef(null);

  // Fetch messages when chatId changes
  useEffect(() => {
    if (currentChatId) {
      fetchChatMessages(currentChatId);
      setShowWelcome(false);
    } else {
      setMessages([]);
      setShowWelcome(true);
    }
  }, [currentChatId]);

  // Auto scroll to bottom when messages update
  useEffect(() => {
    messagesEndRef.current?.scrollIntoView({ behavior: "smooth" });
  }, [messages]);

  // Focus input on new chat trigger
  useEffect(() => {
    if (newChatTrigger > 0) {
      createNewChat();
    }
  }, [newChatTrigger]);

  const fetchChatMessages = async (chatId) => {
    try {
      const response = await axios.get(`/api/chat/${chatId}`);
      setMessages(response.data.chat.messages);
    } catch (err) {
      console.error("Error fetching messages:", err);
    }
  };

  const createNewChat = async () => {
    try {
      const response = await axios.post("/api/chat");
      setCurrentChatId(response.data.chatId);
      setMessages([]);
      setShowWelcome(false);
      setInputValue("");
      setTimeout(() => {
        inputRef.current?.focus();
      }, 100);
    } catch (err) {
      console.error("Error creating new chat:", err);
    }
  };

  const handleSendMessage = async () => {
    if (inputValue.trim() === "") return;

    // Add user message locally
    const userMessage = {
      id: Date.now(),
      text: inputValue,
      sender: "user",
      time: new Date().toLocaleTimeString([], {
        hour: "2-digit",
        minute: "2-digit",
      }),
    };

    setMessages((prev) => [...prev, userMessage]);
    setInputValue("");

    try {
      // Save user message to database
      await axios.post(`/api/chat/${currentChatId}/messages`, {
        text: inputValue,
        sender: "user",
      });

      // Add bot reply after a short delay
      setTimeout(async () => {
        const botReply = {
          id: Date.now() + 1,
          text: "Thank you for your message! This is a static reply for now, but I'll be happy to assist you with any questions or tasks.",
          sender: "bot",
          time: new Date().toLocaleTimeString([], {
            hour: "2-digit",
            minute: "2-digit",
          }),
          likes: 0,
          dislikes: 0,
        };

        setMessages((prev) => [...prev, botReply]);

        // Save bot reply to database
        await axios.post(`/api/chat/${currentChatId}/messages`, {
          text: botReply.text,
          sender: "bot",
        });
      }, 1000);
    } catch (err) {
      console.error("Error sending message:", err);
    }
  };

  const handleReaction = async (messageId, reaction) => {
    try {
      await axios.patch(
        `/api/chat/${currentChatId}/messages/${messageId}/reaction`,
        {
          reaction,
        }
      );

      setMessages((prevMessages) =>
        prevMessages.map((msg) => {
          if (msg.id === messageId) {
            return { ...msg, [reaction]: (msg[reaction] || 0) + 1 };
          }
          return msg;
        })
      );
    } catch (err) {
      console.error("Error updating reaction:", err);
    }
  };
  // Handle Enter key in textarea
  const handleKeyDown = (e) => {
    if (e.key === "Enter" && !e.shiftKey) {
      e.preventDefault();
      handleSendMessage();
    }
  };

  return (
    <main
      className={`flex-1 overflow-hidden p-3 flex flex-col ${
        darkMode ? "bg-gray-900" : "bg-gray-50"
      }`}
    >
      {/* Messages area */}
      <div className="flex-1 overflow-y-auto mb-4 flex flex-col">
        {showWelcome ? (
          <div className="flex-1 flex flex-col items-center justify-center">
            <div className="flex flex-col items-center justify-center mb-6">
              <div className="bg-blue-600 text-white p-3 rounded-full mb-3">
                <MessageSquare size={20} />
              </div>
              <h1 className="text-xl font-bold mb-1">Hello, I'm BotAI</h1>
              <p
                className={`text-sm ${
                  darkMode ? "text-gray-400" : "text-gray-500"
                }`}
              >
                How can I make things easier for you?
              </p>
            </div>

            <div className="w-full max-w-xl mt-6 grid grid-cols-2 sm:grid-cols-3 gap-2">
              {[
                { icon: Image, text: "Image Generator" },
                { icon: Video, text: "Video Generator" },
                { icon: Music, text: "Audio Generator" },
                { icon: Edit, text: "Photo Editor" },
                { icon: BookOpen, text: "Education Feedback" },
                { icon: MessageSquare, text: "Get Advice" },
                { icon: FileCode, text: "Code Generator" },
                { icon: PenLine, text: "Help me write" },
                { icon: FileText, text: "Summarize text" },
              ].map((item, index) => (
                <div
                  key={index}
                  className={`flex items-center p-2 rounded-lg border cursor-pointer hover:shadow-sm ${
                    darkMode
                      ? "bg-gray-800 border-gray-700 hover:bg-gray-700"
                      : "bg-white border-gray-200 hover:bg-gray-50"
                  }`}
                >
                  <item.icon
                    size={16}
                    className={`mr-1.5 ${
                      darkMode ? "text-gray-400" : "text-gray-500"
                    }`}
                  />
                  <span
                    className={`text-xs ${darkMode ? "text-gray-300" : ""}`}
                  >
                    {item.text}
                  </span>
                </div>
              ))}
            </div>
          </div>
        ) : (
          <div className="flex-1 flex flex-col space-y-3 pt-2">
            {messages.map((msg) => (
              <div
                key={msg.id}
                className={`flex ${
                  msg.sender === "user" ? "justify-end" : "justify-start"
                }`}
              >
                <div className="relative group">
                  <div
                    className={`max-w-xs md:max-w-md rounded-lg px-4 py-2 ${
                      msg.sender === "user"
                        ? `${
                            darkMode ? "bg-blue-600" : "bg-blue-500"
                          } text-white`
                        : darkMode
                        ? "bg-gray-800 text-white"
                        : "bg-white text-gray-800 border border-gray-200"
                    }`}
                  >
                    <div className="text-sm">{msg.text}</div>
                    <div className="flex items-center justify-end mt-1 space-x-1">
                      <span
                        className={`text-xs ${
                          msg.sender === "user"
                            ? "text-blue-100"
                            : darkMode
                            ? "text-gray-400"
                            : "text-gray-500"
                        }`}
                      >
                        {msg.time}
                      </span>
                      {msg.sender === "user" && (
                        <span>
                          {messages.length > messages.indexOf(msg) + 1 ? (
                            <CheckCheck size={12} className="text-blue-100" />
                          ) : (
                            <Check size={12} className="text-blue-100" />
                          )}
                        </span>
                      )}
                    </div>
                  </div>

                  {/* Message actions */}
                  <div
                    className={`absolute ${
                      msg.sender === "user"
                        ? "right-full mr-2"
                        : "left-full ml-2"
                    } 
                    top-0 opacity-0 group-hover:opacity-100 transition-opacity flex flex-col gap-1`}
                  >
                    <button
                      onClick={() => copyToClipboard(msg.text, msg.id)}
                      className={`p-1 rounded-full ${
                        darkMode
                          ? "bg-gray-800 hover:bg-gray-700"
                          : "bg-gray-100 hover:bg-gray-200"
                      }`}
                      title="Copy message"
                    >
                      {copyNotification === msg.id ? (
                        <Check
                          size={14}
                          className={
                            darkMode ? "text-green-400" : "text-green-600"
                          }
                        />
                      ) : (
                        <Copy
                          size={14}
                          className={
                            darkMode ? "text-gray-400" : "text-gray-600"
                          }
                        />
                      )}
                    </button>

                    <button
                      onClick={() => replyToMessage(msg.text)}
                      className={`p-1 rounded-full ${
                        darkMode
                          ? "bg-gray-800 hover:bg-gray-700"
                          : "bg-gray-100 hover:bg-gray-200"
                      }`}
                      title="Reply"
                    >
                      <CornerUpRight
                        size={14}
                        className={darkMode ? "text-gray-400" : "text-gray-600"}
                      />
                    </button>

                    {/* Only show like/dislike for bot messages */}
                    {msg.sender === "bot" && (
                      <>
                        <button
                          onClick={() => handleReaction(msg.id, "like")}
                          className={`p-1 rounded-full ${
                            darkMode
                              ? "bg-gray-800 hover:bg-gray-700"
                              : "bg-gray-100 hover:bg-gray-200"
                          }`}
                          title="Like"
                        >
                          <ThumbsUp
                            size={14}
                            className={
                              darkMode ? "text-gray-400" : "text-gray-600"
                            }
                          />
                          {msg.likes > 0 && (
                            <span
                              className={`absolute -top-1 -right-1 text-xs bg-blue-500 text-white rounded-full h-4 w-4 flex items-center justify-center`}
                            >
                              {msg.likes}
                            </span>
                          )}
                        </button>

                        <button
                          onClick={() => handleReaction(msg.id, "dislike")}
                          className={`p-1 rounded-full ${
                            darkMode
                              ? "bg-gray-800 hover:bg-gray-700"
                              : "bg-gray-100 hover:bg-gray-200"
                          }`}
                          title="Dislike"
                        >
                          <ThumbsDown
                            size={14}
                            className={
                              darkMode ? "text-gray-400" : "text-gray-600"
                            }
                          />
                          {msg.dislikes > 0 && (
                            <span
                              className={`absolute -top-1 -right-1 text-xs bg-red-500 text-white rounded-full h-4 w-4 flex items-center justify-center`}
                            >
                              {msg.dislikes}
                            </span>
                          )}
                        </button>
                      </>
                    )}
                  </div>
                </div>
              </div>
            ))}
            <div ref={messagesEndRef} />
          </div>
        )}
      </div>

      {/* Input area */}
      <div
        className={`w-full relative rounded-lg shadow-sm border ${
          darkMode ? "bg-gray-800 border-gray-700" : "bg-white border-gray-200"
        } overflow-hidden`}
      >
        <div className="p-3 min-h-24">
          <textarea
            className={`w-full h-full outline-none resize-none text-sm ${
              darkMode ? "bg-gray-800 text-white placeholder-gray-400" : ""
            }`}
            placeholder="Message BotAI..."
            value={inputValue}
            onChange={(e) => setInputValue(e.target.value)}
            onKeyDown={handleKeyDown}
            rows={1}
            ref={inputRef}
          ></textarea>
        </div>

        <div
          className={`border-t ${
            darkMode ? "border-gray-700" : "border-gray-200"
          } p-2 flex items-center justify-between`}
        >
          <div className="flex">
            <button
              className={`p-1.5 rounded-full ${
                darkMode ? "hover:bg-gray-700" : "hover:bg-gray-100"
              }`}
            >
              <Search
                size={16}
                className={darkMode ? "text-gray-400" : "text-gray-500"}
              />
            </button>
            <button
              className={`p-1.5 rounded-full ${
                darkMode ? "hover:bg-gray-700" : "hover:bg-gray-100"
              }`}
            >
              <Lightbulb
                size={16}
                className={darkMode ? "text-gray-400" : "text-gray-500"}
              />
            </button>
          </div>

          <div className="flex">
            <button
              className={`p-1.5 rounded-full ${
                darkMode ? "hover:bg-gray-700" : "hover:bg-gray-100"
              }`}
            >
              <Mic
                size={16}
                className={darkMode ? "text-gray-400" : "text-gray-500"}
              />
            </button>
            <button
              className={`p-1.5 rounded-full ${
                darkMode ? "hover:bg-gray-700" : "hover:bg-gray-100"
              }`}
            >
              <PenLine
                size={16}
                className={darkMode ? "text-gray-400" : "text-gray-500"}
              />
            </button>
            <button
              className="bg-blue-600 text-white p-1.5 rounded-full hover:bg-blue-700"
              onClick={handleSendMessage}
            >
              <Send size={16} />
            </button>
          </div>
        </div>
      </div>
    </main>
  );
};

export default Chat;
