import React, { useContext, useState } from "react";
import { AuthContext } from "../../Context/AuthContext";
import { Link, useNavigate } from "react-router-dom";

function Signup() {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [error, setError] = useState(null);
  const { register, googleSignIn } = useContext(AuthContext);
  const navigate = useNavigate();

  const validateInputs = () => {
    if (password.length < 8) {
      alert("Password must be at least 8 characters long.");
      return false;
    }
    return true;
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError(""); // Reset error message
    if (!validateInputs()) return;
    try {
      await register(email, password);
      alert("Registration successful! You can now log in.");
      navigate("/");
    } catch (err) {
      setError(err.response?.data?.message || "Registration failed");
    }
  };

  const handleGoogleSignIn = async () => {
    try {
      // Open Google sign-in popup
      const popup = window.open(
        "http://localhost:5000/api/auth/google",
        "_blank",
        "width=500,height=600"
      );

      // Listen for message from the popup
      window.addEventListener("message", (event) => {
        if (event.origin !== "http://localhost:5000") return;

        if (event.data.success) {
          // Handle successful login
          console.log("User data:", event.data.user);
          navigate("/");
        } else {
          setError(event.data.error || "Google sign-in failed");
        }
        popup.close();
      });
    } catch (err) {
      setError("Google sign-in failed");
      console.error(err);
    }
  };
  return (
    <div className="min-h-screen flex items-center justify-center bg-center bg-no-repeat page-bg p-4">
      <style
        dangerouslySetInnerHTML={{
          __html: `
            .page-bg {
              background-image: url('assets/media/images/2600x1200/bg-10.png');
            }
            .dark .page-bg {
              background-image: url('assets/media/images/2600x1200/bg-10-dark.png');
            }
          `,
        }}
      />
      <div className="card max-w-[370px] w-full">
        <form
          className="card-body flex flex-col gap-5 p-10"
          onSubmit={handleSubmit}
        >
          <div className="text-center mb-2.5">
            <h3 className="text-lg font-medium text-gray-900 leading-none mb-2.5">
              Sign up
            </h3>
            <div className="flex items-center justify-center">
              <span className="text-2sm text-gray-700 me-1.5">
                Already have an Account?
              </span>
              <Link className="text-2sm link" to="/">
                Sign In
              </Link>
            </div>
          </div>
          <div className="grid grid-cols-1 gap-2.5">
            <button
              type="button"
              className="btn btn-light btn-sm justify-center"
              onClick={handleGoogleSignIn}
            >
              <img
                alt=""
                className="size-3.5 shrink-0"
                src="assets/media/brand-logos/google.svg"
              />{" "}
              Use Google
            </button>
          </div>
          <div className="flex items-center gap-2">
            <span className="border-t border-gray-200 w-full"></span>
            <span className="text-2xs text-gray-600 uppercase">or</span>
            <span className="border-t border-gray-200 w-full"></span>
          </div>
          <div className="flex flex-col gap-1">
            <label className="form-label text-gray-900">Email</label>
            <input
              className="input"
              type="email"
              placeholder="enter email"
              value={email}
              onChange={(e) => setEmail(e.target.value)}
              required
            />
          </div>
          <div className="flex flex-col gap-1">
            <label className="form-label font-normal text-gray-900">
              Password
            </label>
            <input
              className="input"
              placeholder="password"
              type="password"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
              required
            />
          </div>
          {error && <p className="text-red-500 text-sm">{error}</p>}
          <button
            className="btn btn-primary flex justify-center grow"
            type="submit"
          >
            Sign up
          </button>
        </form>
      </div>
    </div>
  );
}

export default Signup;
