import React, { createContext, useState } from 'react';
import axios from 'axios';

export const AuthContext = createContext();

export const AuthProvider = ({ children }) => {
    const [token, setToken] = useState(null);

    const register = async (passwordConfirmation,first_name,last_name,email, password) => {
        await axios.post('https://emergencytime.techsops.com/api/register', {first_name,last_name,passwordConfirmation, email, password });
    };

   const login = async (email, password) => {
    const response = await axios.post('https://emergencytime.techsops.com/api/login', { email, password });
    setToken(response.data.token);
    localStorage.setItem('token', response.data.token); // ✅ Store in localStorage
};


    return (
        <AuthContext.Provider value={{ token, register, login }}>
            {children}
        </AuthContext.Provider>
    );
};
