import React, { useState } from "react";
import {
  MessageSquare,
  Image,
  HelpCircle,
  FileText,
  ChevronDown,
  Smartphone,
  Award,
  Edit2,
  Trash2,
} from "lucide-react";

const Sidebar = ({
  darkMode,
  sidebarCollapsed,
  setSidebarCollapsed,
  showMore,
  setShowMore,
  onNewChat,
  chats,
  onSelectChat,
  activeChatId,
}) => {
  const [editingChatId, setEditingChatId] = useState(null);
  const [newTitle, setNewTitle] = useState("");

  // Handler for new chat button
  const handleNewChat = () => {
    if (onNewChat) onNewChat();
  };

  // Handler for starting title edit
  const startEditing = (chat) => {
    setEditingChatId(chat._id);
    setNewTitle(chat.title);
  };

  // Handler for saving edited title
  const saveTitle = async (chatId) => {
    try {
      const response = await fetch(`/api/chats/${chatId}/title`, {
        method: "PATCH",
        headers: {
          "Content-Type": "application/json",
          Authorization: `Bearer ${localStorage.getItem("token")}`, // Adjust based on your auth setup
        },
        body: JSON.stringify({ title: newTitle }),
      });
      if (!response.ok) throw new Error("Failed to update title");
      setEditingChatId(null);
      setNewTitle("");
      // Optionally, refresh chats or update state
      onSelectChat(chatId); // Trigger re-render or state update
    } catch (error) {
      console.error("Error updating title:", error);
    }
  };

  // Handler for deleting a chat
  const deleteChat = async (chatId) => {
    if (!window.confirm("Are you sure you want to delete this chat?")) return;
    try {
      const response = await fetch(`/api/chats/${chatId}`, {
        method: "DELETE",
        headers: {
          Authorization: `Bearer ${localStorage.getItem("token")}`, // Adjust based on your auth setup
        },
      });
      if (!response.ok) throw new Error("Failed to delete chat");
      // Optionally, refresh chats or update state
      onNewChat(); // Reset to new chat or update state
    } catch (error) {
      console.error("Error deleting chat:", error);
    }
  };

  return (
    <div
      className={`${darkMode ? "bg-gray-800" : "bg-white"} ${
        sidebarCollapsed ? "w-16" : "w-52"
      } flex flex-col border-r ${
        darkMode ? "border-gray-700" : "border-gray-200"
      } transition-all duration-300`}
    >
      <div className="p-3 flex items-center">
        <div className="bg-blue-600 text-white p-1.5 rounded-full mr-2">
          <MessageSquare size={16} />
        </div>
        {!sidebarCollapsed && (
          <span className="font-bold text-base">BotAI</span>
        )}
      </div>

      <div className="px-2 py-2">
        <button
          className="bg-blue-600 hover:bg-blue-700 text-white rounded-lg p-2 w-full flex items-center justify-center"
          onClick={handleNewChat}
        >
          {sidebarCollapsed ? (
            <MessageSquare size={16} />
          ) : (
            <>
              <MessageSquare size={16} className="mr-2" />
              <span className="text-sm">New Chat</span>
            </>
          )}
        </button>
      </div>

      <div className={`px-2 py-1 ${sidebarCollapsed ? "items-center" : ""}`}>
        <div
          className={`flex items-center p-1.5 rounded-lg ${
            darkMode ? "hover:bg-gray-700" : "hover:bg-gray-100"
          } cursor-pointer`}
        >
          <Image
            size={16}
            className={`mr-2 ${darkMode ? "text-gray-400" : "text-gray-500"}`}
          />
          {!sidebarCollapsed && <span className="text-sm">AI Generator</span>}
        </div>
        <div
          className={`flex items-center p-1.5 rounded-lg ${
            darkMode ? "hover:bg-gray-700" : "hover:bg-gray-100"
          } cursor-pointer`}
        >
          <HelpCircle
            size={16}
            className={`mr-2 ${darkMode ? "text-gray-400" : "text-gray-500"}`}
          />
          {!sidebarCollapsed && <span className="text-sm">Explore BotAI</span>}
        </div>
      </div>

      {!sidebarCollapsed && (
        <>
          <div className="mt-2 px-2">
            <div
              className={`text-xs ${
                darkMode ? "text-gray-400" : "text-gray-500"
              } mb-1`}
            >
              Chats
            </div>
            {chats.map((chat) => (
              <div
                key={chat._id}
                className={`flex items-center p-1.5 rounded-lg ${
                  activeChatId === chat._id
                    ? darkMode
                      ? "bg-gray-700"
                      : "bg-gray-100"
                    : darkMode
                    ? "hover:bg-gray-700"
                    : "hover:bg-gray-100"
                } cursor-pointer`}
              >
                <FileText
                  size={14}
                  className={`mr-1.5 ${
                    darkMode ? "text-gray-400" : "text-gray-500"
                  }`}
                />
                {editingChatId === chat._id ? (
                  <div className="flex items-center w-full">
                    <input
                      type="text"
                      value={newTitle}
                      onChange={(e) => setNewTitle(e.target.value)}
                      className={`text-xs bg-transparent border-b ${
                        darkMode ? "border-gray-600" : "border-gray-300"
                      } focus:outline-none`}
                    />
                    <button
                      onClick={() => saveTitle(chat._id)}
                      className="ml-2 text-blue-500"
                    >
                      Save
                    </button>
                    <button
                      onClick={() => setEditingChatId(null)}
                      className="ml-2 text-gray-500"
                    >
                      Cancel
                    </button>
                  </div>
                ) : (
                  <>
                    <span
                      className="text-xs truncate flex-1"
                      onClick={() => onSelectChat(chat._id)}
                    >
                      {chat.title}
                    </span>
                    <div className="flex space-x-2">
                      <Edit2
                        size={14}
                        className={`${
                          darkMode ? "text-gray-400" : "text-gray-500"
                        } hover:text-blue-500`}
                        onClick={() => startEditing(chat)}
                      />
                      <Trash2
                        size={14}
                        className={`${
                          darkMode ? "text-gray-400" : "text-gray-500"
                        } hover:text-red-500`}
                        onClick={() => deleteChat(chat._id)}
                      />
                    </div>
                  </>
                )}
              </div>
            ))}
          </div>

          <div className="mt-2 px-2">
            <div
              className={`flex items-center p-1.5 rounded-lg ${
                darkMode ? "hover:bg-gray-700" : "hover:bg-gray-100"
              } cursor-pointer`}
              onClick={() => setShowMore(!showMore)}
            >
              <ChevronDown
                size={14}
                className={`mr-1.5 ${
                  darkMode ? "text-gray-400" : "text-gray-500"
                } ${showMore ? "transform rotate-180" : ""}`}
              />
              <span className="text-xs">More</span>
            </div>
            {showMore && (
              <div className="ml-4 mt-1">
                <div
                  className={`flex items-center p-1.5 rounded-lg ${
                    darkMode ? "hover:bg-gray-700" : "hover:bg-gray-100"
                  } cursor-pointer`}
                >
                  <FileText
                    size={14}
                    className={`mr-1.5 ${
                      darkMode ? "text-gray-400" : "text-gray-500"
                    }`}
                  />
                  <span className="text-xs truncate">Additional item 1</span>
                </div>
                <div
                  className={`flex items-center p-1.5 rounded-lg ${
                    darkMode ? "hover:bg-gray-700" : "hover:bg-gray-100"
                  } cursor-pointer`}
                >
                  <FileText
                    size={14}
                    className={`mr-1.5 ${
                      darkMode ? "text-gray-400" : "text-gray-500"
                    }`}
                  />
                  <span className="text-xs truncate">Additional item 2</span>
                </div>
              </div>
            )}
          </div>
        </>
      )}

      <div className="mt-auto">
        <div className="px-2 py-1">
          <div
            className={`flex items-center p-1.5 rounded-lg ${
              darkMode ? "hover:bg-gray-700" : "hover:bg-gray-100"
            } cursor-pointer`}
          >
            <HelpCircle
              size={16}
              className={`mr-1.5 ${
                darkMode ? "text-gray-400" : "text-gray-500"
              }`}
            />
            {!sidebarCollapsed && <span className="text-sm">Support</span>}
          </div>
          <div
            className={`flex items-center p-1.5 rounded-lg ${
              darkMode ? "hover:bg-gray-700" : "hover:bg-gray-100"
            } cursor-pointer`}
          >
            <MessageSquare
              size={16}
              className={`mr-1.5 ${
                darkMode ? "text-gray-400" : "text-gray-500"
              }`}
            />
            {!sidebarCollapsed && <span className="text-sm">Custom Bots</span>}
          </div>
          <div
            className={`flex items-center p-1.5 rounded-lg ${
              darkMode ? "hover:bg-gray-700" : "hover:bg-gray-100"
            } cursor-pointer`}
          >
            <Award
              size={16}
              className={`mr-1.5 ${
                darkMode ? "text-gray-400" : "text-gray-500"
              }`}
            />
            {!sidebarCollapsed && <span className="text-sm">Settings</span>}
          </div>
        </div>

        <div
          className={`px-2 py-2 border-t ${
            darkMode ? "border-gray-700" : "border-gray-200"
          }`}
        >
          <button className="bg-blue-50 text-blue-600 hover:bg-blue-100 rounded-lg p-1.5 w-full flex items-center justify-center">
            {sidebarCollapsed ? (
              <Smartphone size={16} />
            ) : (
              <>
                <Smartphone size={16} className="mr-1.5" />
                <span className="text-xs">Get App</span>
                <span className="ml-auto text-xs bg-blue-100 px-1 py-0.5 rounded text-xs">
                  New
                </span>
              </>
            )}
          </button>
        </div>
      </div>
    </div>
  );
};

export default Sidebar;
