import React, { useContext } from "react";
import { ChevronLeft, ChevronRight, Sun, Moon, Share } from "lucide-react";
import { AuthContext } from "../Context/AuthContext";

const Header = ({
  darkMode,
  setDarkMode,
  sidebarCollapsed,
  setSidebarCollapsed,
}) => {
  const { user } = useContext(AuthContext);

  // Function to get user's initial
  const getUserInitial = () => {
    if (user && user.name) {
      return user.name.charAt(0).toUpperCase();
    }
    return "U"; // Default if no name is available
  };

  // Function to generate a color based on user's email
  const getUserColor = () => {
    if (!user || !user.email) return "bg-blue-500";

    const hash = user.email.split("").reduce((acc, char) => {
      return char.charCodeAt(0) + acc;
    }, 0);

    const colors = [
      "bg-blue-500",
      "bg-green-500",
      "bg-purple-500",
      "bg-red-500",
      "bg-yellow-500",
      "bg-pink-500",
    ];

    return colors[hash % colors.length];
  };

  return (
    <header
      className={`border-b ${
        darkMode ? "border-gray-700" : "border-gray-200"
      } py-1.5 px-3 flex items-center justify-between`}
    >
      {/* ... rest of your header code ... */}
      <div className="flex items-center">
        <button
          onClick={() => setSidebarCollapsed(!sidebarCollapsed)}
          className={`p-1.5 rounded-full ${
            darkMode ? "hover:bg-gray-700" : "hover:bg-gray-100"
          }`}
        >
          {sidebarCollapsed ? (
            <ChevronRight size={16} />
          ) : (
            <ChevronLeft size={16} />
          )}
        </button>
      </div>

      <div className="flex items-center">
        <button
          className={`p-1.5 rounded-full ${
            darkMode ? "hover:bg-gray-700" : "hover:bg-gray-100"
          } mr-1.5`}
          onClick={() => setDarkMode(!darkMode)}
        >
          {darkMode ? <Sun size={16} /> : <Moon size={16} />}
        </button>
        <button
          className={`p-1.5 rounded-full ${
            darkMode ? "hover:bg-gray-700" : "hover:bg-gray-100"
          } mr-1.5`}
        >
          <Share size={16} className={darkMode ? "text-gray-300" : ""} />
        </button>
        <div
          className={`w-5 h-5 ${getUserColor()} rounded-full flex items-center justify-center text-white text-xs font-medium`}
        >
          {getUserInitial()}
        </div>
      </div>
    </header>
  );
};

export default Header;
