import React, { useState, useEffect } from "react";
import {
  MessageSquare,
  Image,
  HelpCircle,
  FileText,
  ChevronDown,
  Smartphone,
  Award,
} from "lucide-react";
import axios from "axios";

const Sidebar = ({
  darkMode,
  sidebarCollapsed,
  setSidebarCollapsed,
  showMore,
  setShowMore,
  onNewChat,
  onSelectChat,
  currentChatId,
}) => {
  const [chats, setChats] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchChats = async () => {
      try {
        const response = await axios.get("/api/chats");
        setChats(response.data);
        setLoading(false);
      } catch (error) {
        console.error("Error fetching chats:", error);
        setLoading(false);
      }
    };
    fetchChats();
  }, []);

  const formatDate = (dateString) => {
    const date = new Date(dateString);
    const now = new Date();
    const diffDays = Math.floor((now - date) / (1000 * 60 * 60 * 24));

    if (diffDays === 0) return "Today";
    if (diffDays === 1) return "Yesterday";
    if (diffDays < 7) return `${diffDays} days ago`;
    if (diffDays < 30) return `${Math.floor(diffDays / 7)} weeks ago`;
    return date.toLocaleDateString();
  };

  return (
    <div
      className={`${darkMode ? "bg-gray-800" : "bg-white"} ${
        sidebarCollapsed ? "w-16" : "w-52"
      } flex flex-col border-r ${
        darkMode ? "border-gray-700" : "border-gray-200"
      } transition-all duration-300`}
    >
      <div className="p-3 flex items-center">
        <div className="bg-blue-600 text-white p-1.5 rounded-full mr-2">
          <MessageSquare size={16} />
        </div>
        {!sidebarCollapsed && (
          <span className="font-bold text-base">BotAI</span>
        )}
      </div>

      <div className="px-2 py-2">
        <button
          className="bg-blue-600 hover:bg-blue-700 text-white rounded-lg p-2 w-full flex items-center justify-center"
          onClick={onNewChat}
        >
          {sidebarCollapsed ? (
            <MessageSquare size={16} />
          ) : (
            <>
              <MessageSquare size={16} className="mr-2" />
              <span className="text-sm">New Chat</span>
            </>
          )}
        </button>
      </div>

      <div className={`px-2 py-1 ${sidebarCollapsed ? "items-center" : ""}`}>
        <div
          className={`flex items-center p-1.5 rounded-lg ${
            darkMode ? "hover:bg-gray-700" : "hover:bg-gray-100"
          } cursor-pointer`}
        >
          <Image
            size={16}
            className={`mr-2 ${darkMode ? "text-gray-400" : "text-gray-500"}`}
          />
          {!sidebarCollapsed && <span className="text-sm">AI Generator</span>}
        </div>
        <div
          className={`flex items-center p-1.5 rounded-lg ${
            darkMode ? "hover:bg-gray-700" : "hover:bg-gray-100"
          } cursor-pointer`}
        >
          <HelpCircle
            size={16}
            className={`mr-2 ${darkMode ? "text-gray-400" : "text-gray-500"}`}
          />
          {!sidebarCollapsed && <span className="text-sm">Explore BotAI</span>}
        </div>
      </div>

      {!sidebarCollapsed && !loading && (
        <div className="flex-1 overflow-y-auto">
          {chats.map((chat) => (
            <div
              key={chat._id}
              className={`flex items-center p-2 mx-2 my-1 rounded-lg cursor-pointer ${
                chat._id === currentChatId
                  ? darkMode
                    ? "bg-gray-700"
                    : "bg-gray-200"
                  : darkMode
                  ? "hover:bg-gray-700"
                  : "hover:bg-gray-100"
              }`}
              onClick={() => onSelectChat(chat._id, chat.title)}
            >
              <FileText
                size={14}
                className={`mr-2 ${
                  darkMode ? "text-gray-400" : "text-gray-500"
                }`}
              />
              <div className="flex-1 min-w-0">
                <p className="text-sm truncate">{chat.title}</p>
                <p className="text-xs text-gray-500">
                  {formatDate(chat.updatedAt)}
                </p>
              </div>
            </div>
          ))}
        </div>
      )}

      <div className="mt-auto">
        <div className="px-2 py-1">
          <div
            className={`flex items-center p-1.5 rounded-lg ${
              darkMode ? "hover:bg-gray-700" : "hover:bg-gray-100"
            } cursor-pointer`}
          >
            <HelpCircle
              size={16}
              className={`mr-1.5 ${
                darkMode ? "text-gray-400" : "text-gray-500"
              }`}
            />
            {!sidebarCollapsed && <span className="text-sm">Support</span>}
          </div>
          <div
            className={`flex items-center p-1.5 rounded-lg ${
              darkMode ? "hover:bg-gray-700" : "hover:bg-gray-100"
            } cursor-pointer`}
          >
            <MessageSquare
              size={16}
              className={`mr-1.5 ${
                darkMode ? "text-gray-400" : "text-gray-500"
              }`}
            />
            {!sidebarCollapsed && <span className="text-sm">Custom Bots</span>}
          </div>
          <div
            className={`flex items-center p-1.5 rounded-lg ${
              darkMode ? "hover:bg-gray-700" : "hover:bg-gray-100"
            } cursor-pointer`}
          >
            <Award
              size={16}
              className={`mr-1.5 ${
                darkMode ? "text-gray-400" : "text-gray-500"
              }`}
            />
            {!sidebarCollapsed && <span className="text-sm">Settings</span>}
          </div>
        </div>

        <div
          className={`px-2 py-2 border-t ${
            darkMode ? "border-gray-700" : "border-gray-200"
          }`}
        >
          <button className="bg-blue-50 text-blue-600 hover:bg-blue-100 rounded-lg p-1.5 w-full flex items-center justify-center">
            {sidebarCollapsed ? (
              <Smartphone size={16} />
            ) : (
              <>
                <Smartphone size={16} className="mr-1.5" />
                <span className="text-xs">Get App</span>
                <span className="ml-auto text-xs bg-blue-100 px-1 py-0.5 rounded text-xs">
                  New
                </span>
              </>
            )}
          </button>
        </div>
      </div>
    </div>
  );
};

export default Sidebar;
