import React, { useState } from "react";
import {
  MessageSquare,
  Search,
  Lightbulb,
  Mic,
  PenLine,
  Send,
  Image,
  Video,
  Music,
  Edit,
  BookOpen,
  FileCode,
  FileText,
} from "lucide-react";

const Chat = ({ darkMode }) => {
  const [messages, setMessages] = useState([]);
  const [input, setInput] = useState("");

  const handleSend = () => {
    if (!input.trim()) return;

    const time = new Date().toLocaleTimeString([], {
      hour: "2-digit",
      minute: "2-digit",
    });

    const newMessage = {
      sender: "user",
      text: input,
      time,
    };

    const reply = {
      sender: "bot",
      text: "Thanks for your message! I'm here to help.",
      time,
    };

    setMessages([...messages, newMessage, reply]);
    setInput("");
  };

  return (
    <main className="flex-1 overflow-y-auto p-3 flex flex-col items-center">
      <div className="flex flex-col items-center justify-center mb-6">
        <div className="bg-blue-600 text-white p-3 rounded-full mb-3">
          <MessageSquare size={20} />
        </div>
        <h1 className="text-xl font-bold mb-1">Hello, I'm BotAI</h1>
        <p
          className={`text-sm ${darkMode ? "text-gray-400" : "text-gray-500"}`}
        >
          How can I make things easier for you?
        </p>
      </div>

      <div className={`w-full max-w-xl flex flex-col gap-3 mb-4`}>
        {messages.map((msg, idx) => (
          <div
            key={idx}
            className={`max-w-[75%] p-2 rounded-lg text-sm ${
              msg.sender === "user"
                ? `self-end bg-blue-600 text-white`
                : `self-start ${
                    darkMode
                      ? "bg-gray-700 text-white"
                      : "bg-gray-100 text-black"
                  }`
            }`}
          >
            <div>{msg.text}</div>
            <div className="text-[10px] text-right opacity-70 mt-1">
              {msg.time}
            </div>
          </div>
        ))}
      </div>

      <div
        className={`w-full max-w-xl relative rounded-lg shadow-sm border ${
          darkMode ? "bg-gray-800 border-gray-700" : "bg-white border-gray-200"
        } overflow-hidden`}
      >
        <div className="p-3 min-h-24">
          <textarea
            value={input}
            onChange={(e) => setInput(e.target.value)}
            className={`w-full h-full outline-none resize-none text-sm ${
              darkMode ? "bg-gray-800 text-white placeholder-gray-400" : ""
            }`}
            placeholder="Message BotAI..."
          ></textarea>
        </div>

        <div
          className={`border-t ${
            darkMode ? "border-gray-700" : "border-gray-200"
          } p-2 flex items-center justify-between`}
        >
          <div className="flex">
            {[Search, Lightbulb].map((Icon, idx) => (
              <button
                key={idx}
                className={`p-1.5 rounded-full ${
                  darkMode ? "hover:bg-gray-700" : "hover:bg-gray-100"
                }`}
              >
                <Icon
                  size={16}
                  className={darkMode ? "text-gray-400" : "text-gray-500"}
                />
              </button>
            ))}
          </div>

          <div className="flex gap-2">
            {[Mic, PenLine].map((Icon, idx) => (
              <button
                key={idx}
                className={`p-1.5 rounded-full ${
                  darkMode ? "hover:bg-gray-700" : "hover:bg-gray-100"
                }`}
              >
                <Icon
                  size={16}
                  className={darkMode ? "text-gray-400" : "text-gray-500"}
                />
              </button>
            ))}
            <button
              onClick={handleSend}
              className="bg-blue-600 text-white p-1.5 rounded-full hover:bg-blue-700"
            >
              <Send size={16} />
            </button>
          </div>
        </div>
      </div>

      <div className="w-full max-w-xl mt-6 grid grid-cols-2 sm:grid-cols-3 gap-2">
        {[
          { icon: Image, text: "Image Generator" },
          { icon: Video, text: "Video Generator" },
          { icon: Music, text: "Audio Generator" },
          { icon: Edit, text: "Photo Editor" },
          { icon: BookOpen, text: "Education Feedback" },
          { icon: MessageSquare, text: "Get Advice" },
          { icon: FileCode, text: "Code Generator" },
          { icon: PenLine, text: "Help me write" },
          { icon: FileText, text: "Summarize text" },
        ].map((item, index) => (
          <div
            key={index}
            className={`flex items-center p-2 rounded-lg border cursor-pointer hover:shadow-sm ${
              darkMode
                ? "bg-gray-800 border-gray-700 hover:bg-gray-700"
                : "bg-white border-gray-200 hover:bg-gray-50"
            }`}
          >
            <item.icon
              size={16}
              className={`mr-1.5 ${
                darkMode ? "text-gray-400" : "text-gray-500"
              }`}
            />
            <span className={`text-xs ${darkMode ? "text-gray-300" : ""}`}>
              {item.text}
            </span>
          </div>
        ))}
      </div>
    </main>
  );
};

export default Chat;
