import React, { useState, useEffect } from "react";
import {
  MessageSquare,
  Image,
  HelpCircle,
  FileText,
  ChevronDown,
  Smartphone,
  Award,
  Plus,
  Trash2,
} from "lucide-react";
import axios from "axios";

const Sidebar = ({
  darkMode,
  sidebarCollapsed,
  setSidebarCollapsed,
  showMore,
  setShowMore,
  onNewChat,
  currentChatId,
  setCurrentChatId,
  user,
}) => {
  const [chats, setChats] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    fetchChats();
  }, []);

  const fetchChats = async () => {
    try {
      setLoading(true);
      const response = await axios.get("/api/chat");
      setChats(response.data.chats);
    } catch (err) {
      console.error("Error fetching chats:", err);
    } finally {
      setLoading(false);
    }
  };

  const handleChatClick = (chatId) => {
    setCurrentChatId(chatId);
  };

  const handleNewChat = async () => {
    try {
      const response = await axios.post("/api/chat");
      setCurrentChatId(response.data.chatId);
      fetchChats(); // Refresh the chat list
      if (onNewChat) onNewChat();
    } catch (err) {
      console.error("Error creating new chat:", err);
    }
  };

  const deleteChat = async (chatId, e) => {
    e.stopPropagation();
    try {
      await axios.delete(`/api/chat/${chatId}`);
      if (currentChatId === chatId) {
        setCurrentChatId(null);
      }
      fetchChats();
    } catch (err) {
      console.error("Error deleting chat:", err);
    }
  };

  // Group chats by date (simplified for example)
  const recentChats = [...chats]
    .sort((a, b) => new Date(b.createdAt) - new Date(a.createdAt))
    .slice(0, 2);
  const last7DaysChats = [...chats]
    .sort((a, b) => new Date(b.createdAt) - new Date(a.createdAt))
    .slice(2, 4);
  const last30DaysChats = [...chats]
    .sort((a, b) => new Date(b.createdAt) - new Date(a.createdAt))
    .slice(4);

  return (
    <div
      className={`${darkMode ? "bg-gray-800" : "bg-white"} ${
        sidebarCollapsed ? "w-16" : "w-52"
      } flex flex-col border-r ${
        darkMode ? "border-gray-700" : "border-gray-200"
      } transition-all duration-300 h-full`}
    >
      <div className="p-3 flex items-center justify-between">
        <div className="flex items-center">
          <div className="bg-blue-600 text-white p-1.5 rounded-full mr-2">
            <MessageSquare size={16} />
          </div>
          {!sidebarCollapsed && (
            <span className="font-bold text-base">BotAI</span>
          )}
        </div>
        {!sidebarCollapsed && (
          <button
            onClick={() => setSidebarCollapsed(!sidebarCollapsed)}
            className={`p-1 rounded-full ${
              darkMode ? "hover:bg-gray-700" : "hover:bg-gray-100"
            }`}
          >
            <ChevronDown
              size={16}
              className={`transform rotate-90 ${
                darkMode ? "text-gray-400" : "text-gray-500"
              }`}
            />
          </button>
        )}
      </div>

      <div className={`px-2 py-2 ${sidebarCollapsed ? "items-center" : ""}`}>
        <button
          className="bg-blue-600 hover:bg-blue-700 text-white rounded-lg p-2 w-full flex items-center justify-center"
          onClick={handleNewChat}
        >
          {sidebarCollapsed ? (
            <Plus size={16} />
          ) : (
            <>
              <Plus size={16} className="mr-2" />
              <span className="text-sm">New Chat</span>
            </>
          )}
        </button>
      </div>

      {!sidebarCollapsed && (
        <>
          <div className="mt-2 px-2">
            <div
              className={`text-xs ${
                darkMode ? "text-gray-400" : "text-gray-500"
              } mb-1`}
            >
              Recent
            </div>
            {loading ? (
              <div className="animate-pulse space-y-2">
                {[...Array(2)].map((_, i) => (
                  <div
                    key={`skeleton-${i}`}
                    className={`h-8 rounded-lg ${
                      darkMode ? "bg-gray-700" : "bg-gray-200"
                    }`}
                  ></div>
                ))}
              </div>
            ) : (
              recentChats.map((chat) => (
                <div
