import React, { createContext, useState } from "react";
import axios from "axios";

export const AuthContext = createContext();

export const AuthProvider = ({ children }) => {
  const [token, setToken] = useState(null);
  const [user, setUser] = useState(null);

  const register = async (email, password, first_name, last_name) => {
    await axios.post("http://localhost:5000/api/signup", {
      email,
      password,
      last_name,
      first_name,
    });
  };

  const login = async (email, password) => {
    const response = await axios.post("http://localhost:5000/api/signin", {
      email,
      password,
    });
    setToken(response.data.token);
    localStorage.setItem("token", response.data.token); // ✅ Store in localStorage
  };
  const googleSignIn = async () => {
    try {
      // Open Google sign-in popup
      const googleAuthUrl = `http://localhost:5000/api/google-signin`;
      const popup = window.open(
        googleAuthUrl,
        "_blank",
        "width=500,height=600"
      );

      // Listen for message from the popup
      return new Promise((resolve, reject) => {
        const messageListener = (event) => {
          if (event.origin !== "http://localhost:5000") return;

          if (event.data.success) {
            setUser(event.data.user);
            localStorage.setItem("token", event.data.token);
            popup.close();
            window.removeEventListener("message", messageListener);
            resolve(event.data);
          } else {
            popup.close();
            window.removeEventListener("message", messageListener);
            reject(new Error(event.data.error || "Google sign-in failed"));
          }
        };

        window.addEventListener("message", messageListener);
      });
    } catch (error) {
      throw error;
    }
  };

  return (
    <AuthContext.Provider
      value={{ token, user, register, login, googleSignIn }}
    >
      {children}
    </AuthContext.Provider>
  );
};
