import React, { useState, useEffect } from "react";
import {
  MessageSquare,
  Image,
  HelpCircle,
  FileText,
  ChevronDown,
  Smartphone,
  Award,
} from "lucide-react";
import axios from "axios";

const Sidebar = ({
  darkMode,
  sidebarCollapsed,
  setSidebarCollapsed,
  showMore,
  setShowMore,
  onNewChat,
  setConversationId,
}) => {
  const [conversations, setConversations] = useState([]);

  useEffect(() => {
    fetchConversations();
  }, []);

  const fetchConversations = async () => {
    try {
      const token = localStorage.getItem("token");
      const response = await axios.get("/api/chat/conversations", {
        headers: { Authorization: `Bearer ${token}` },
      });
      setConversations(response.data);
    } catch (err) {
      console.error("Error fetching conversations:", err);
    }
  };

  const handleNewChat = () => {
    if (onNewChat) onNewChat();
  };

  const handleConversationSelect = (id) => {
    setConversationId(id);
  };

  // Group conversations by time periods
  const recentChats = conversations.filter((conv) => {
    const diff =
      (new Date() - new Date(conv.createdAt)) / (1000 * 60 * 60 * 24);
    return diff <= 1;
  });

  const last7DaysChats = conversations.filter((conv) => {
    const diff =
      (new Date() - new Date(conv.createdAt)) / (1000 * 60 * 60 * 24);
    return diff > 1 && diff <= 7;
  });

  const last30DaysChats = conversations.filter((conv) => {
    const diff =
      (new Date() - new Date(conv.createdAt)) / (1000 * 60 * 60 * 24);
    return diff > 7 && diff <= 30;
  });

  return (
    <div
      className={`${darkMode ? "bg-gray-800" : "bg-white"} ${
        sidebarCollapsed ? "w-16" : "w-52"
      } flex flex-col border-r ${
        darkMode ? "border-gray-700" : "border-gray-200"
      } transition-all duration-300`}
    >
      <div className="p-3 flex items-center">
        <div className="bg-blue-600 text-white p-1.5 rounded-full mr-2">
          <MessageSquare size={16} />
        </div>
        {!sidebarCollapsed && (
          <span className="font-bold text-base">BotAI</span>
        )}
      </div>
      <div className="px-2 py-2">
        <button
          className="bg-blue-600 hover:bg-blue-700 text-white rounded-lg p-2 w-full flex items-center justify-center"
          onClick={handleNewChat}
        >
          {sidebarCollapsed ? (
            <MessageSquare size={16} />
          ) : (
            <>
              <MessageSquare size={16} className="mr-2" />
              <span className="text-sm">New Chat</span>
            </>
          )}
        </button>
      </div>
      <div className={`px-2 py-1 ${sidebarCollapsed ? "items-center" : ""}`}>
        <div
          className={`flex items-center p-1.5 rounded-lg ${
            darkMode ? "hover:bg-gray-700" : "hover:bg-gray-100"
          } cursor-pointer`}
        >
          <Image
            size={16}
            className={`mr-2 ${darkMode ? "text-gray-400" : "text-gray-500"}`}
          />
          {!sidebarCollapsed && <span className="text-sm">AI Generator</span>}
        </div>
        <div
          className={`flex items-center p-1.5 rounded-lg ${
            darkMode ? "hover:bg-gray-700" : "hover:bg-gray-100"
          } cursor-pointer`}
        >
          <HelpCircle
            size={16}
            className={`mr-2 ${darkMode ? "text-gray-400" : "text-gray-500"}`}
          />
          {!sidebarCollapsed && <span className="text-sm">Explore BotAI</span>}
        </div>
      </div>
      {!sidebarCollapsed && (
        <>
          <div className="mt-2 px-2">
            <div
              className={`text-xs ${
                darkMode ? "text-gray-400" : "text-gray-500"
              } mb-1`}
            >
              Recent
            </div>
            {recentChats.map((chat) => (
              <div
                key={chat._id}
                className={`flex items-center p-1.5 rounded-lg ${
                  darkMode ? "hover:bg-gray-700" : "hover:bg-gray-100"
                } cursor-pointer`}
                onClick={() => handleConversationSelect(chat._id)}
              >
                <FileText
                  size={14}
                  className={`mr-1.5 ${
                    darkMode ? "text-gray-400" : "text-gray-500"
                  }`}
                />
                <span className="text-xs truncate">{chat.title}</span>
              </div>
            ))}
          </div>
          <div className="mt-2 px-2">
            <div
              className={`text-xs ${
                darkMode ? "text-gray-400" : "text-gray-500"
              } mb-1`}
            >
              Last 7 Days
            </div>
            {last7DaysChats.map((chat) => (
              <div
                key={chat._id}
                className={`flex items-center p-1.5 rounded-lg ${
                  darkMode ? "hover:bg-gray-700" : "hover:bg-gray-100"
                } cursor-pointer`}
                onClick={() => handleConversationSelect(chat._id)}
              >
                <FileText
                  size={14}
                  className={`mr-1.5 ${
                    darkMode ? "text-gray-400" : "text-gray-500"
                  }`}
                />
                <span className="text-xs truncate">{chat.title}</span>
              </div>
            ))}
          </div>
          <div className="mt-2 px-2">
            <div
              className={`text-xs ${
                darkMode ? "text-gray-400" : "text-gray-500"
              } mb-1`}
            >
              Last 30 Days
            </div>
            {last30DaysChats.map((chat) => (
              <div
                key={chat._id}
                className={`flex items-center p-1.5 rounded-lg ${
                  darkMode ? "hover:bg-gray-700" : "hover:bg-gray-100"
                } cursor-pointer`}
                onClick={() => handleConversationSelect(chat._id)}
              >
                <FileText
                  size={14}
                  className={`mr-1.5 ${
                    darkMode ? "text-gray-400" : "text-gray-500"
                  }`}
                />
                <span className="text-xs truncate">{chat.title}</span>
              </div>
            ))}
          </div>
          <div className="mt-1 px-2">
            <div
              className={`flex items-center p-1.5 rounded-lg ${
                darkMode ? "hover:bg-gray-700" : "hover:bg-gray-100"
              } cursor-pointer`}
              onClick={() => setShowMore(!showMore)}
            >
              <ChevronDown
                size={14}
                className={`mr-1.5 ${
                  darkMode ? "text-gray-400" : "text-gray-500"
                } ${showMore ? "transform rotate-180" : ""}`}
              />
              <span className="text-xs">More</span>
            </div>
            {showMore && (
              <div className="ml-4 mt-1">
                {conversations
                  .filter((conv) => {
                    const diff =
                      (new Date() - new Date(conv.createdAt)) /
                      (1000 * 60 * 60 * 24);
                    return diff > 30;
                  })
                  .map((chat) => (
                    <div
                      key={chat._id}
                      className={`flex items-center p-1.5 rounded-lg ${
                        darkMode ? "hover:bg-gray-700" : "hover:bg-gray-100"
                      } cursor-pointer`}
                      onClick={() => handleConversationSelect(chat._id)}
                    >
                      <FileText
                        size={14}
                        className={`mr-1.5 ${
                          darkMode ? "text-gray-400" : "text-gray-500"
                        }`}
                      />
                      <span className="text-xs truncate">{chat.title}</span>
                    </div>
                  ))}
              </div>
            )}
          </div>
        </>
      )}
      <div className="mt-auto">
        <div className="px-2 py-1">
          <div
            className={`flex items-center p-1.5 rounded-lg ${
              darkMode ? "hover:bg-gray-700" : "hover:bg-gray-100"
            } cursor-pointer`}
          >
            <HelpCircle
              size={16}
              className={`mr-1.5 ${
                darkMode ? "text-gray-400" : "text-gray-500"
              }`}
            />
            {!sidebarCollapsed && <span className="text-sm">Support</span>}
          </div>
          <div
            className={`flex items-center p-1.5 rounded-lg ${
              darkMode ? "hover:bg-gray-700" : "hover:bg-gray-100"
            } cursor-pointer`}
          >
            <MessageSquare
              size={16}
              className={`mr-1.5 ${
                darkMode ? "text-gray-400" : "text-gray-500"
              }`}
            />
            {!sidebarCollapsed && <span className="text-sm">Custom Bots</span>}
          </div>
          <div
            className={`flex items-center p-1.5 rounded-lg ${
              darkMode ? "hover:bg-gray-700" : "hover:bg-gray-100"
            } cursor-pointer`}
          >
            <Award
              size={16}
              className={`mr-1.5 ${
                darkMode ? "text-gray-400" : "text-gray-500"
              }`}
            />
            {!sidebarCollapsed && <span className="text-sm">Settings</span>}
          </div>
        </div>
        <div
          className={`px-2 py-2 border-t ${
            darkMode ? "border-gray-700" : "border-gray-200"
          }`}
        >
          <button className="bg-blue-50 text-blue-600 hover:bg-blue-100 rounded-lg p-1.5 w-full flex items-center justify-center">
            {sidebarCollapsed ? (
              <Smartphone size={16} />
            ) : (
              <>
                <Smartphone size={16} className="mr-1.5" />
                <span className="text-xs">Get App</span>
                <span className="ml-auto text-xs bg-blue-100 px-1 py-0.5 rounded text-xs">
                  New
                </span>
              </>
            )}
          </button>
        </div>
      </div>
    </div>
  );
};

export default Sidebar;
