import React, { useContext, useState } from "react";
import { AuthContext } from "../../Context/AuthContext";
import { Link, useNavigate } from "react-router-dom";

function Signup() {
  // const [first_name, setfirst_name] = useState("");
  // const [last_name, setlast_name] = useState("");
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  // const [passwordConfirmation, setPasswordConfirmation] = useState("");
  const [error, setError] = useState(null);
  const { register } = useContext(AuthContext);

  const navigate = useNavigate();

  const validateInputs = () => {
    // const nameRegex = /^[A-Za-z]+$/;
    // if (!nameRegex.test(first_name) || !nameRegex.test(last_name)) {
    //   alert("Name should only contain letters.");
    //   return false;
    // }
    if (password.length < 8) {
      alert("Password must be at least 8 characters long.");
      return false;
    }
    return true;
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError(""); // Reset error message

    if (!validateInputs()) return;

    try {
      const result = await register(email, password);
      alert("Registration successful! You can now log in.");
      navigate("/");
    } catch (err) {
      console.error("Registration Error:", err.response?.data || err.message);
      setError(err.response?.data?.message || "Registration failed");
    }
  };

  return (
    <div className="min-h-screen flex items-center justify-center bg-center bg-no-repeat page-bg p-4">
      <style
        dangerouslySetInnerHTML={{
          __html: `
            .page-bg {
              background-image: url('assets/media/images/2600x1200/bg-10.png');
            }
            .dark .page-bg {
              background-image: url('assets/media/images/2600x1200/bg-10-dark.png');
            }
          `,
        }}
      />
      <div className="card max-w-[370px] w-full">
        <form
          className="card-body flex flex-col gap-5 p-10"
          onSubmit={handleSubmit}
        >
          <div className="text-center mb-2.5">
            <h3 className="text-lg font-medium text-gray-900 leading-none mb-2.5">
              Sign up
            </h3>
            <div className="flex items-center justify-center">
              <span className="text-2sm text-gray-700 me-1.5">
                Already have an Account?
              </span>
              <Link className="text-2sm link" to="/">
                Sign In
              </Link>
            </div>
          </div>
          <div className="grid grid-cols-2 gap-2.5">
            <a className="btn btn-light btn-sm justify-center" href="#">
              <img
                alt=""
                className="size-3.5 shrink-0"
                src="assets/media/brand-logos/google.svg"
              />{" "}
              Use Google
            </a>
            <a className="btn btn-light btn-sm justify-center" href="#">
              <img
                alt=""
                className="size-3.5 shrink-0 dark:hidden"
                src="assets/media/brand-logos/apple-black.svg"
              />
              <img
                alt=""
                className="size-3.5 shrink-0 light:hidden"
                src="assets/media/brand-logos/apple-white.svg"
              />{" "}
              Use Apple
            </a>
          </div>
          <div className="flex items-center gap-2">
            <span className="border-t border-gray-200 w-full"></span>
            <span className="text-2xs text-gray-600 uppercase">or</span>
            <span className="border-t border-gray-200 w-full"></span>
          </div>
          {/* <div className="flex flex-col gap-1">
            <label className="form-label text-gray-900">First Name</label>
            <input
              className="input"
              type="text"
              value={first_name}
              onChange={(e) => setfirst_name(e.target.value)}
              required
            />
          </div>
          <div className="flex flex-col gap-1">
            <label className="form-label text-gray-900">Second Name</label>
            <input
              className="input"
              type="text"
              value={last_name}
              onChange={(e) => setlast_name(e.target.value)}
              required
            />
          </div> */}
          <div className="flex flex-col gap-1">
            <label className="form-label text-gray-900">Email</label>
            <input
              className="input"
              type="email"
              value={email}
              onChange={(e) => setEmail(e.target.value)}
              required
            />
          </div>
          <div className="flex flex-col gap-1">
            <label className="form-label font-normal text-gray-900">
              Password
            </label>
            <input
              className="input"
              type="password"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
              required
            />
          </div>
          {/* <div className="flex flex-col gap-1">
            <label className="form-label font-normal text-gray-900">
              Confirm Password
            </label>
            <input
              className="input"
              type="password"
              value={passwordConfirmation}
              onChange={(e) => setPasswordConfirmation(e.target.value)}
              required
            />
          </div> */}
          {error && <p className="text-red-500 text-sm">{error}</p>}
          <button
            className="btn btn-primary flex justify-center grow"
            type="submit"
          >
            Sign up
          </button>
        </form>
      </div>
    </div>
  );
}

export default Signup;
